import React, { Component } from 'react'
import './App.css';
import {Route, Link} from "react-router-dom"
import Home from "./components/Home/Home"
import AboutMe from "./components/AboutMe/AboutMe"
import Projects from "./components/Projects/Projects"
import Contact from "./components/Contact/Contact"
class App extends Component {
  constructor(props) {
      super(props)

      this.state = {
          openNav: false
      }

      this.revealNav = this.revealNav.bind(this)
      this.hideNav = this.hideNav.bind(this)
  }

  revealNav() {
      this.setState({
          openNav: true
      })
      console.log('show navbar')
  }

  hideNav() {
      this.setState({
          openNav: false
      })
      console.log('hide navbar')
  }

  render() {
    return (
      <div className="App">
        <div className="toggle" onClick={this.revealNav}>
          <span></span>
          <span></span>
          <span></span>
        </div>

        <nav>
          <button onClick={this.hideNav}>x</button>
          <ul id='nav-links'>
            <h4><Link to="/">Home</Link></h4>
            <h4><Link to="about-me">About Me</Link></h4>
            <h4><Link to="projects">Projects</Link></h4>
            <h4><Link to="contact">Contact</Link></h4>
          </ul>
        </nav>

        <main>
          <Route exact path="/" component={Home}></Route>
          <Route path="/about-me" component={AboutMe}></Route>
          <Route path="/projects" component={Projects}></Route>
          <Route path="/contact" component={Contact}></Route>
        </main>
      </div>
    );
  }
}

export default App;
