import React, { Component } from 'react'
import Carousel from 'react-bootstrap/Carousel';
import './Projects.css'

class Slideshow extends React.Component {
    constructor(props, context) {
        super(props, context)

        this.handleSelect = this.handleSelect.bind(this);

        this.state = {
            index: 0,
            direction: null,
            images: [
                require("./app-images/breaking-bricks-home.png"),
                require("./app-images/breaking-bricks-start.png"),
                require("./app-images/breaking-bricks-win.png"),
                require("./app-images/breaking-bricks-lose.png"),

            ]
        }
    }

    handleSelect(selectedIndex, e) {
            this.setState({
            index: selectedIndex,
            direction: e.direction,
        });
    }

    render() {
        const { index, direction, images } = this.state;

        return (
            <div className="slideshow-section">
                <Carousel
                    className="carousel"
                    activeIndex={index}
                    direction={direction}
                    onSelect={this.handleSelect}>

                    <Carousel.Item className="carousel-image">
                        <img
                            className="d-block w-100"
                            src={images[0]}
                            alt="First slide"
                        />
                        <Carousel.Caption>
                        </Carousel.Caption>
                    </Carousel.Item>

                    <Carousel.Item>
                        <img
                            className="d-block w-100"
                            src={images[0]}
                            alt="Third slide"
                        />
                        <Carousel.Caption>

                        </Carousel.Caption>
                    </Carousel.Item>

                    <Carousel.Item>
                        <img
                            className="d-block w-100"
                            src={images[2]}
                            alt="Third slide"
                        />
                        <Carousel.Caption>
                        </Carousel.Caption>
                    </Carousel.Item>

                    <Carousel.Item>
                        <img
                            className="d-block w-100"
                            src={images[3]}
                            alt="Third slide"
                        />
                        <Carousel.Caption>
                        </Carousel.Caption>
                    </Carousel.Item>

                </Carousel>
            </div>
        );
    }
}

export default Slideshow
