import React, { Component } from 'react'
import "./Navbar.css"

class ShowNav extends Component {
    constructor(props) {
        super(props)

        this.state = {
            openNav: false
        }

        this.revealNav = this.revealNav.bind(this)
    }

    revealNav() {
        this.setState({
            openNav: true
        })
        console.log('show navbar')
    }

    render() {
        return (
            <div>
                <div className="toggle" onClick={this.props.toggleNav}>
                    <span></span>
                    <span></span>
                    <span></span>
                </div>
            </div>
        )
    }
}

export default ShowNav
