import React, { Component } from 'react'
import './App.css';
import { Route } from "react-router-dom"
import Home from "./components/Home/Home"
import AboutMe from "./components/AboutMe/AboutMe"
import Projects from "./components/Projects/Projects"
import Contact from "./components/Contact/Contact"
import HideNav from "./components/Navbar/HideNav"
import ShowNav from "./components/Navbar/ShowNav"

class App extends Component {
  constructor() {
    super()

    this.state = {
      revealNav: false
    }
    this.toggleRevealNav = this.toggleRevealNav.bind(this)
  }

  toggleRevealNav() {
    this.setState((prevState) => {
      return {revealNav: !prevState.revealNav}
    })
  }

  render() {
    return (
      <div className="App">
        <ShowNav />
        <HideNav />
        <main>
          <Route exact path="/" component={Home}></Route>
          <Route path="/about-me" component={AboutMe}></Route>
          <Route path="/projects" component={Projects}></Route>
          <Route path="/contact" component={Contact}></Route>
        </main>
      </div>
    );
  }
}

export default App;
