import React, { Component } from 'react'

class HambburgerMenu extends Component {
    constructor(props) {
        super(props)

        this.state = {
            openNav: false
        }

        this.revealNav = this.revealNav.bind(this)
        this.hideNav = this.hideNav.bind(this)
    }

    revealNav() {
        this.setState({
            openNav: true
        })
        console.log('show navbar')
    }

    hideNav() {
        this.setState({
            openNav: false
        })
        console.log('hide navbar')
    }

    render() {
        return (
            <div>
                <div className="toggle" onClick={this.revealNav}>
                    <span></span>
                    <span></span>
                    <span></span>
                </div>
            </div>
        )
    }
}

export default HambburgerMenu
